import React, { Component } from 'react'
import './navigation.css';

import CategoryNatureBlank from './svg/nature-blank.svg';
import CategoryIndustrialBlank from './svg/industrial-blank.svg';
import CategoryWatersideBlank from './svg/waterside-blank.svg';
import CategoryStreetBlank from './svg/street-blank.svg';

import HighlightTitle from './svg/highlight.svg';
import MapTitle from './svg/map.svg';

let nature = CategoryNatureBlank;
let industrial = CategoryIndustrialBlank;
let waterside = CategoryWatersideBlank;
let street = CategoryStreetBlank;


let activeId = 38;
let activeCategory = 0;

let category1 = [1,17,18,22,34,40,49,55,58,61,72];

// Categories

// const Category = props => {
//   let categoryClass = 'category category-unknown'; //navigation.css

//   if (props.active) {
//     categoryClass = 'category category-discovered';
//   }

//   return (
//     <div
//       className={categoryClass}
//       id={props.key}
//       key={props.key}
//       onClick={props.categorySelect}
//     > </div>
//   )
// }

// Circle

const FragmentCircle = props => {
  let circleClass = 'circle circle-unknown'; //navigation.css

  if (props.discovered) {
    circleClass = 'circle circle-discovered';
  }
  if (props.active) {
    circleClass = 'circle circle-active';
  }

  if (props.highlighted) {
    circleClass += ' circle-highlighted';
  }

  return (
    <div
      className={circleClass}
      id={props.key}
      key={props.key}
      onClick={props.handleClick}
    />
  )
}

// Deault matrix

const buildMatrixState = (rowCount, rowLength) => {
  const totalCircles = rowCount * rowLength;
  const matrixState = {};

  // Calculate details for each circle
  for (let key = 1; key <= totalCircles; key++) {
    const circleDetails = {
      active: false,
      discovered: false,
      highlighted: false
    };

    matrixState[key] = circleDetails;
  }
  matrixState[activeId].active = true;

  return matrixState;
}

// Class

class Navigation extends Component {
  constructor(props) {
    super(props)
    const initialRowCount = 15;
    const initialRowLength = 5;

    this.state = {
      rowCount: initialRowCount,
      rowLength: initialRowLength,
      matrix: buildMatrixState(initialRowCount, initialRowLength)
    };

    this.handleClick = this.handleClick.bind(this);
  }

  // cleanHighlighted(matrix) {
  //   for (let key = 1; key <= 75; key++) {
  //     matrix[key].highlighted = false;
  //   }
  //   return matrix;
  // }

  categoryHover(index) {
    // if (index === 1) {
    //   nature = CategoryNatureActive;
    // } else if (index === 2) {
    //   industrial = CategoryIndustrialActive;
    // }
  }

  categorySelect(index) {
    const matrix = this.state.matrix;

    if (index === 0) {
      for (let key = 1; key <= 75; key++) {
        matrix[key].highlighted = false;
      }
    } else if (index === 1) {
      for (let key = 0; key < category1.length; key++) {
        matrix[category1[key]].highlighted = true;
      }
    }
    //this.state.matrix = matrix;
    this.setState({ matrix })
  }

  handleClick(e) {
    const matrix = this.state.matrix;
    const id = e.target.id;

    matrix[id].active = true;
    matrix[activeId].active = false;
    matrix[activeId].discovered = true;

    this.setState({ matrix });
    activeId = id;
    this.props.handleIdChange(id);
  }

  rowBuilder(rowCount, rowLength, rows = []) {
    if (rowCount > 0) {
      const circleKey = rowCount * rowLength;
      rows.push(this.circleBuilder(circleKey, rowCount, rowLength));

      return this.rowBuilder(rowCount - 1, rowLength, rows);
    }
    return rows
  }

  circleBuilder(circleKey, rowCount, rowLength, row = []) {
    if (rowLength > 0) {
      const circle = this.state.matrix[circleKey];

      row.push(FragmentCircle({
        key: circleKey,
        active: circle.active,
        discovered: circle.discovered,
        highlighted: circle.highlighted,
        handleClick: this.handleClick,
      }));

      return this.circleBuilder(circleKey - 1, rowCount, rowLength - 1, row);
    }
    return (<div key={'row-' + rowCount}>{row}</div>);
  }

  render() {
    return (
      <div>
        <div className="navigation">
          <img src={MapTitle} alt='MAP' className="map" />
          {this.rowBuilder(this.state.rowCount, this.state.rowLength)}
        </div>
        <div className="cats">
          <img src={nature} alt='nature' className="category-nature" onClick={this.categorySelect(1)} />
          <img src={industrial} alt='industrial' className="category-industrial" onClick={this.categorySelect(1)} />
          <img src={waterside} alt='waterside' className="category-waterside" onClick={this.categorySelect(1)} />
          <img src={street} alt='street' className="category-street" onClick={this.categorySelect(0)} />

        </div>
        <img src={HighlightTitle} alt='HIGHLIGHT' className="highlight" />
      </div>
    )
  }
}

export default Navigation;
