import React from "react";
import * as THREE from "three";
import { OBJLoader } from "three/examples/jsm/loaders/OBJLoader";
import SimplexNoise from"simplex-noise"
import "./three.css";

let scene = new THREE.Scene();
let camera = new THREE.PerspectiveCamera( 45, window.innerWidth / window.innerHeight, 1, 2000 );
let renderer = new THREE.WebGLRenderer( { alpha: true } );

var myObjPromise, myObjPromiseOriginal;

camera.position.z =250;

renderer.setSize( window.innerWidth, window.innerHeight );
renderer.setPixelRatio( window.devicePixelRatio );
renderer.setClearColor( 0x000000, 1);

const circle = new THREE.TextureLoader().load( './data/circle.png' );

class ThreeStatic extends React.Component {

    constructor(props) {
        super(props); 
        this.state = {
            activeId: props.dataAppToThreeStatic,
            // scene: new THREE.Scene(),
            // camera: new THREE.PerspectiveCamera( 45, window.innerWidth / window.innerHeight, 1, 2000 ),
            // renderer: new THREE.WebGLRenderer( { alpha: true } )
        }
        // this.state.camera.position.z =250;

        // this.state.renderer.setSize( window.innerWidth, window.innerHeight );
        // this.state.renderer.setPixelRatio( window.devicePixelRatio );
        // this.state.renderer.setClearColor( 0x000000, 1);
    }

    static getDerivedStateFromProps(props, state) {
        return {activeId: props.dataAppToThreeMusic}
    }

    threeObject() {
        // this.state.scene = new THREE.Scene();  // TOTO TREBA OPRAVIT
        // var scene = this.state.scene;
        // var camera = this.state.camera;
        // var renderer = this.state.renderer;

        // this.mount.appendChild( this.state.renderer.domElement );

        var activeId = this.state.activeId;

        var mouseX = 0, mouseY = 0;
        var windowHalfX = window.innerWidth / 2;
        var windowHalfY = window.innerHeight / 2;

        var noise = new SimplexNoise();

        // AUDIO
        var context = new AudioContext();
        var analyser = context.createAnalyser();
        analyser.fftSize = 256;
        analyser.connect(context.destination); 

        var dataArray = new Uint8Array(analyser.frequencyBinCount);

        var callback = function(stream) {
            var mic = context.createMediaStreamSource(stream);
            mic.connect(analyser);
        }

        navigator.getUserMedia = navigator.getUserMedia || navigator.webkitGetUserMedia || navigator.mozGetUserMedia;
        navigator.getUserMedia({ video : false, audio : true }, callback, console.log);


        //
        //
        // OBJ LOADER & MODEL SETUP

        const objSize = 61669;

        var loadModel = function(url) {
            return new Promise(resolve => {
                new OBJLoader().load(url, resolve);
            }); 
        }

        // var myObjPromise, myObjPromiseOriginal;

        if (activeId % 7 === 0) {
            myObjPromise = loadModel('./data/strom-61669.obj');
            myObjPromiseOriginal = loadModel('./data/strom-61669.obj');
        } else if (activeId % 7 === 1) {
            myObjPromise = loadModel('./data/lavicka-61186.obj');
            myObjPromiseOriginal = loadModel('./data/lavicka-61186.obj');
        } else if (activeId % 3 === 2) {
            myObjPromise = loadModel('./data/pozor-48737.obj');
            myObjPromiseOriginal = loadModel('./data/pozor-48737.obj');
        } else if (activeId % 7 === 3) {
            myObjPromise = loadModel('./data/1.obj');
            myObjPromiseOriginal = loadModel('./data/1.obj');
        } else if (activeId % 7 === 4) {
            myObjPromise = loadModel('./data/2.obj');
            myObjPromiseOriginal = loadModel('./data/2.obj');
        } else if (activeId % 7 === 5) {
            myObjPromise = loadModel('./data/3.obj');
            myObjPromiseOriginal = loadModel('./data/3.obj');
        } else {
            myObjPromise = loadModel('./data/4.obj');
            myObjPromiseOriginal = loadModel('./data/4.obj');
        }

        myObjPromise.then(myObj => {
            var material = myObj.children[0].material;

            material.size = 3;
            material.sizeAttenuation = true;
            material.map = circle;
            material.alphaTest = 0.5;
            material.transparent = true;

            myObj.scale.x = 100;
            myObj.scale.y = 100;
            myObj.scale.z = 100;

            scene.add(myObj);
            material.dispose();
        });

        //
        //
        // HELPER FUNCTIONS

        var onWindowResize = function() {
            windowHalfX = window.innerWidth / 2;
            windowHalfY = window.innerHeight / 2;

            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();

            renderer.setSize( window.innerWidth, window.innerHeight );
        }

        var onDocumentMouseMove = function(event) {
            mouseX = ( - event.clientX + windowHalfX ) / 2;
            mouseY = ( - event.clientY + windowHalfY ) / 2;
        }

        document.addEventListener( 'mousemove', onDocumentMouseMove );
        window.addEventListener( 'resize', onWindowResize );

        var fractionate = function(val, minVal, maxVal) {
            return (val - minVal)/(maxVal - minVal);
        }

        var modulate = function(val, minVal, maxVal, outMin, outMax) {
            var fr = fractionate(val, minVal, maxVal);
            var delta = outMax - outMin;
            return outMin + (fr * delta);
        }

        //
        //
        // ANIMATE

        var animate = function () {
            requestAnimationFrame( animate );

            analyser.getByteFrequencyData(dataArray);

			var lowerHalfArray = dataArray.slice(0, (dataArray.length/2) - 1);
            // var upperHalfArray = dataArray.slice((dataArray.length/2) - 1, dataArray.length - 1);

            var lowerSum = lowerHalfArray.reduce((a, b) => a + b, 0);
            var lowerLength = lowerHalfArray.length;
            var lowerAvg = (lowerSum / lowerLength) || 0;
            var lowerAvgFr = lowerAvg / lowerLength;
            var distortionFr = modulate(lowerAvgFr, 0, 1, 0, 4);

            myObjPromise.then(myObj => {

                const positions = myObj.children[0].geometry.attributes.position.array;

                myObj.scale.x = 100;
                myObj.scale.y = 100;
                myObj.scale.z = 100;

                myObjPromiseOriginal.then(myObj2 => {
                    scene.add( myObj );
                    const positions2 = myObj2.children[0].geometry.attributes.position.array;

                    myObj2.scale.x = 100;
                    myObj2.scale.y = 100;
                    myObj2.scale.z = 100;

                    for ( let i = 0; i < objSize; i ++ ) {
                        // var offset = 20;
                        var amp = 0.5;
                        var time = window.performance.now();

                        var vertex = new THREE.Vector3(positions2[i*3], positions2[i*3+1], positions2[i*3+2]);

                        var distance = (noise.noise2D(vertex.x + time * 0.0003, vertex.y + time * 0.0001) + 0) * distortionFr * amp;

                        // positions[i*3] = vertex.x + distance;
                        // positions[i*3+1] = vertex.y + distance;
                        positions[i*3+2] = vertex.z + (distance*vertex.z);
                    }
                });

                myObj.children[0].geometry.attributes.position.needsUpdate = true;
            });

            camera.position.x += ( mouseX - camera.position.x ) * .05;
            camera.position.y += ( - mouseY - camera.position.y ) * .05;

            camera.lookAt( scene.position );

            renderer.render( scene, camera );
        };

        animate();

        //return () => myObjPromise.remove();
    }

    componentDidMount() {
        this.mount.appendChild( renderer.domElement );

        if (myObjPromise !== undefined) {
            myObjPromise.then(myObj => {
                myObj.children[0].material.dispose();
                myObj.children[0].geometry.dispose();
                scene.remove(myObj);
            });
            myObjPromise.dispose();

            myObjPromiseOriginal.then(myObj => {
                myObj.children[0].material.dispose();
                myObj.children[0].geometry.dispose();
                scene.remove(myObj);
            });

            renderer.renderLists.dispose();
        }

        this.threeObject();
    }

    componentDidUpdate() {
        myObjPromise.then(myObj => {
            myObj.children[0].material.dispose();
            myObj.children[0].geometry.dispose();
            scene.remove(myObj);
        });
        myObjPromise.dispose();

        myObjPromiseOriginal.then(myObj => {
            myObj.children[0].material.dispose();
            myObj.children[0].geometry.dispose();
            scene.remove(myObj);
        });

        console.log(renderer.info.programs);

        renderer.renderLists.dispose();

        this.threeObject()
    }

    componentWillUnmount() {
        myObjPromise.then(myObj => {
            myObj.children[0].material.dispose();
            myObj.children[0].geometry.dispose();
            scene.remove(myObj);
        });
        myObjPromise.dispose();

        myObjPromiseOriginal.then(myObj => {
            myObj.children[0].material.dispose();
            myObj.children[0].geometry.dispose();
            scene.remove(myObj);
        });

        renderer.renderLists.dispose();
    }

    render() {
        return (
            <div ref={ref => (this.mount = ref)} className = "object"/>
        )
    } 
}

export default ThreeStatic;
