import React, { useRef, useEffect, useState }  from 'react';
import mapboxgl from 'mapbox-gl';
import './map.css';

//let discoveredIds = {};
let locationLng = [0.0, 48.148711, 48.1111, 45.132123, 12.122, 17.2222, 2.222];
let locationLat = [0.0, 17.090743, 17.0800, 16.9992, 18.222, 16.887, 7.2];

const Map = () => {
  const mapContainer = useRef();
  const [lng, setLng] = useState(locationLng[1]);
  const [lat, setLat] = useState(locationLng[1]);
  const [zoom, setZoom] = useState(16);

  useEffect(() => {
    const map = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [lng, lat],
      zoom: zoom
    });

    map.on('move', () => {
      setLng(map.getCenter().lng.toFixed(4));
      setLat(map.getCenter().lat.toFixed(4));
      //setZoom(map.getZoom().toFixed(2));
    });

    return () => map.remove();
  }, []);

  return (
    <div className="map-container" ref={mapContainer} />
  );
};

export default Map;
