import React, { useRef, useEffect } from 'react';
import mapboxgl from 'mapbox-gl';
import './map.css';

let activeId = 38;
let discoveredIds = {};
let locationLon = [0.0, 48.148711];
let locationLat = [0.0, 17.090743];

mapboxgl.accessToken = 'pk.eyJ1Ijoic2FtLWFudCIsImEiOiJSLUx1SURJIn0.QTPlnr_Xse6dYc96m47H4A';

class Map extends React.Component {
  constructor(props) {
    super.props();
    // this.state = {
    //   //activeId: props.dataAppToMap,

    // }
  }

  static getDerivedStateFromProps(props, state) {
    return {activeId: props.dataAppToThreeMap}
  }

  mapBackground = () => {
    const mapContainerRef = useRef(null);

    // initialize map when component mounts
    useEffect(() => {
      const map = new mapboxgl.Map({
        container: mapContainerRef.current,
        style: 'mapbox://styles/sam-ant/cknyvea7s1tmy17pj49f2i8gl',
        center: [locationLat[1], locationLon[1]],
        zoom: 16,
      });

      // add navigation control (the +/- zoom buttons)
      //map.addControl(new mapboxgl.NavigationControl(), 'bottom-right');

      // clean up on unmount
      return () => map.remove();
    }, []); // eslint-disable-line react-hooks/exhaustive-deps

    return <div className="map-container" ref={mapContainerRef} />;
  };
}

export default Map;
