import React from 'react';
import mapboxgl from 'mapbox-gl';
import './map.css';

//let discoveredIds = {};
let locationLng = [0.0, 48.148711, 48.1111, 45.132123, 12.122, 17.2222, 2.222];
let locationLat = [0.0, 17.090743, 17.0800, 16.9992, 18.222, 16.887, 7.2];

mapboxgl.accessToken = 'pk.eyJ1Ijoic2FtLWFudCIsImEiOiJSLUx1SURJIn0.QTPlnr_Xse6dYc96m47H4A';

class Map extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      activeId: props.dataAppToMap,
      map: null,
      lng: locationLng[38%7],
      lat: locationLat[38%7]
    }

    this.mapContainer = React.createRef();
  }

  static getDerivedStateFromProps(props, state) {
    return {activeId: props.dataAppToMap};
  }

  componentDidMount() {
    const { lng, lat } = this.state;

    const mapip = new mapboxgl.Map({
      container: this.mapContainer.current,
      style: 'mapbox://styles/sam-ant/cknyvea7s1tmy17pj49f2i8gl',
      center: [lng, lat],
      zoom: 16
    });

    mapip.on('move', () => {
      this.setState({
        lng: mapip.getCenter().lng.toFixed(4),
        lat: mapip.getCenter().lat.toFixed(4),
      });
      console.log("som v move");
    });

    this.setState({map:mapip});

    console.log("mount hotovka pre ", this.state.activeId);
  }

  componentDidUpdate() {
    console.log("update pre ", this.state.activeId);
    console.log("moja mapa: ", this.state.map);

    //this.state.map.setState()
    this.state.map.flyTo([this.state.lng, this.state.lat], 2500);
    //this.setState({})
    //console.log("flyTo done");
  }

  render() {
    return <div ref={this.mapContainer} className="map-container" />
  }
}

export default Map;
