import React from 'react';
import mapboxgl from 'mapbox-gl';
import './map.css';

//let discoveredIds = {};
let locationLon = [0.0, 48.148711, 48.1111, 45.132123, 12.122, 17.2222, 2.222];
let locationLat = [0.0, 17.090743, 17.0800, 16.9992, 18.222, 16.887, 7.2];

mapboxgl.accessToken = 'pk.eyJ1Ijoic2FtLWFudCIsImEiOiJSLUx1SURJIn0.QTPlnr_Xse6dYc96m47H4A';

class Map extends React.PureComponent {
  constructor(props) {
    super(props);

    this.mapContainer = React.createRef();

    this.state = {
      activeId: props.dataAppToMap,
      //map: new mapboxgl.Map({})
      map: new mapboxgl.Map({
        container: this.mapContainer.current,
        style: 'mapbox://styles/sam-ant/cknyvea7s1tmy17pj49f2i8gl',
        center: [locationLat[17.090743], locationLon[48.148711]],
        zoom: 16
      })
    }

    // this.state.map.setState({
    //   container: this.mapContainer.current,
    //   style: 'mapbox://styles/sam-ant/cknyvea7s1tmy17pj49f2i8gl',
    //   center: [locationLat[17.090743], locationLon[48.148711]],
    //   zoom: 16
    // });
  }

  static getDerivedStateFromProps(props, state) {
    return {activeId: props.dataAppToMap};
  }

  // mapBackground() {
    // const map = new mapboxgl.Map({
    //   container: this.mapContainer.current,
    //   style: 'mapbox://styles/sam-ant/cknyvea7s1tmy17pj49f2i8gl',
    //   center: [locationLat[this.state.activeId%7], locationLon[this.state.activeId%7]],
    //   zoom: 16
    // });
  //   //console.log(this.state.activeId);

  //   map.on('move', () => {
  //     this.setState({
  //       lng: map.getCenter().lng.toFixed(4),
  //       lat: map.getCenter().lat.toFixed(4),
  //       zoom: map.getZoom().toFixed(2)
  //     });
  //   });

  //   return () => map.remove();
  // }

  componentDidMount() {
    let map = this.state.map;

    this.state.map.on('move', () => {
      this.setState({
        lng: map.getCenter().lng.toFixed(4),
        lat: map.getCenter().lat.toFixed(4),
        zoom: map.getZoom().toFixed(2)
      });
    });
  }

  componentDidUpdate() {
    this.state.map.flyTo([locationLat[this.state.activeId%7], locationLon[this.state.activeId%7]], 2500)
  }

  render() {
    return <div ref={this.mapContainer} className="map-container" />
  }
}

export default Map;
