import React, { Component } from 'react'
import './about.css';

import vhCheck from 'vh-check'

class About extends Component {
    constructor(props) {
      super(props);

      this.handleInfo = this.handleInfo.bind(this);
    }

    handleInfo() {
        this.props.handleInfoChange(false);
    }

    // topBuilder() {
    //     return (
    //       <div>
    //         <div
    //             className={'back'}
    //             onClick={this.handleInfo}
    //         />
    //         <div
    //             className={'ordinaments-logo'}
    //         />  
    //       </div>      
    //     );
    // }

    render() {
        vhCheck('browser-address-bar');
        if (window.innerWidth <= 500) {
            return(
                <div>
                    <div className={'top-container'}>
                        <div
                            className={'back'}
                            onClick={this.handleInfo}
                        />
                        <div
                            className={'ordinaments-logo'}
                        />

                    </div>
                    <div className='info-container'>
                        <div className={'m-instruction desc'}>Ordinaments are fragments of reality. They are mostly ordinary objects which are not hard to find. The commonness of ordinaments makes them overlooked and underestimated for residents of the city. But a closer look can reveal their uniqueness.</div>
                        <div className={'m-instruction desc'}>Ordinaments are captured by 3D scanning technology which isolates small fragments from the surroundings, the rest is left to viewer's imagination. The sound completes the atmosphere of the specific moment.</div>
                    </div>

                    <div className={'m-instruction-container'}>
                        <div className={'m-instruction'}>1 highlight category you’re interested in <br/><div className={'desc'}>nature, industrial, street, or waterside</div></div>
                        <div className={'m-instruction'}>2 discover ordinaments on the map <br/><div className={'desc'}><div className={'c c-unknown'}/> represents unknown ordinament <br/><div className={'c c-discovered'}/> is discovered <br/><div className={'c c-highlighted'}/> is highlighted <br/><div className={'c c-active'}/> is currently open</div></div>
                    </div>
                </div>
            );
        } else {
            return(
                <div>
                    <div className={'top-container'}>
                        <div
                            className={'back'}
                            onClick={this.handleInfo}
                        />
                        <div
                            className={'ordinaments-logo'}
                        />
                        <div className={'info-container'}>
                            <div className={'instruction desc'}>Ordinaments are fragments of reality. They are mostly ordinary objects which are not hard to find. The commonness of ordinaments makes them overlooked and underestimated for residents of the city. But a closer look can reveal their uniqueness.</div>
                            <div className={'instruction desc'}>Ordinaments are captured by 3D scanning technology which isolates small fragments from the surroundings, the rest is left to viewer's imagination. The sound completes the atmosphere of the specific moment.</div>
                        </div>
                    </div>

                    <div className={'instruction-container'}>
                        <div className={'instruction'}>1 highlight category you’re interested in <br/><div className={'desc'}>nature, industrial, street, or waterside</div></div>
                        <div className={'instruction'}>2 discover ordinaments on the map <br/><div className={'desc'}><div className={'c c-unknown'}/> represents unknown ordinament <br/><div className={'c c-discovered'}/> is discovered <br/><div className={'c c-highlighted'}/> is highlighted <br/><div className={'c c-active'}/> is currently open</div></div>
                        <div className={'instruction'}>3 click <div className={'music'}/> in the top right corner to turn on music mode <br/><div className={'desc'}>then play your favourite music on your computer</div></div>
                    </div>
                </div>
            );
        }
    }
}

export default About;
