import React from "react";
import ReactDOM from "react-dom";
import * as THREE from "three";
import { OBJLoader } from "three/examples/jsm/loaders/OBJLoader";
//OBJLoader(THREE);

class App extends React.Component {
    componentDidMount() {
        var scene = new THREE.Scene();
        var camera = new THREE.PerspectiveCamera( 45, window.innerWidth / window.innerHeight, 1, 2000 );
        camera.position.z = 5;

        var renderer = new THREE.WebGLRenderer();
        renderer.setSize( window.innerWidth, window.innerHeight );
        this.mount.appendChild( renderer.domElement );

        // var objLoader = new OBJLoader();
        // objLoader.load("./data/strom-61669.obj",
        //     object => {this.freedomMesh = object;
        //         this.freedomMesh.position.setY(3); //or  this
        //         this.freedomMesh.scale.set(0.02, 0.02, 0.02);
        //         scene.add(this.freedomMesh);
        //     }, xhr => {
        //         console.log((xhr.loaded / xhr.total) * 100 + "% loaded");
        //     }, error => {
        //         console.log("An error happened" + error);
        //     });

        // OBJ LOADER
        var loadModel = function() {
            var progress = console.log;
            return new Promise(function( resolve, reject ){
                //this.THREE = THREE;
                const objLoader = new OBJLoader();
                objLoader.load( './data/strom-61669.obj', resolve, progress, reject );
            });
        }
        // MODEL SETUP
        var myObjPromise = loadModel();

        myObjPromise.then(myObj => {
            //var geometry = myObj.geometry;
            var material = myObj.children[0].material;

            const circle = new THREE.TextureLoader().load( './data/circle.png' );

            material.size = 3;
            material.sizeAttenuation = true;
            material.map = circle;
            material.alphaTest = 0.5;
            material.transparent = true;

            myObj.scale.x = 100;
            myObj.scale.y = 100;
            myObj.scale.z = 100;
        });

        const light = new THREE.HemisphereLight( 0xffffbb, 0x080820, 1 );
        scene.add( light );

        var animate = function () {
            requestAnimationFrame( animate );

            myObjPromise.then(myObj => {
              scene.add( myObj );
            });

            renderer.render( scene, camera );
        };

        animate();
    }
    render() {
        return (
            <div ref={ref => (this.mount = ref)} />
        )
    }
}const rootElement = document.getElementById("root")
ReactDOM.render(<App />, rootElement);
export default App;
