import React, { Component } from 'react';
// import { Redirect } from 'react-router-dom';
import Navigation from './Navigation.js';
import ThreeObject from './ThreeObject.js';
import Sounds from './Sounds.js';
import Map from './Map.js';
import MapMusic from './MapMusic.js';
import Cursor from './Cursor.js';
import About from './About.js';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      width: window.innerWidth,
      activeId: 38,
      music: false,
      info: false
    }
  }

  componentWillMount() {
    window.addEventListener('resize', this.handleWindowSizeChange);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.handleWindowSizeChange);
  }

  handleIdChange = (index) => {
    this.setState({activeId: index});
  }

  handleMusicChange = (boo) => {
    this.setState({music: boo});
  }

  handleInfoChange = (boo) => {
    this.setState({info: boo});
  }

  renderStatic() {
    return (
      <div>
        <Map dataAppToMap = {this.state.activeId}/>
        <div>
          <ThreeObject dataAppToThreeObject1 = {this.state.activeId} dataAppToThreeObject2 = {this.state.music}/>
          <Sounds dataAppToSounds = {this.state.activeId}/>
          <div>
            <Cursor />
            <Navigation
              activeId={this.state.activeId} 
              handleIdChange={this.handleIdChange}
              handleMusicChange={this.handleMusicChange}
              handleInfoChange={this.handleInfoChange}
            />
          </div>
        </div>
      </div>
    );
  }

  renderMusic() {
    return (
      <div>
        <MapMusic dataAppToMap = {this.state.activeId}/>
        <div>
          <ThreeObject dataAppToThreeObject1 = {this.state.activeId} dataAppToThreeObject2 = {this.state.music}/>
          <div>
            <Cursor />
            <Navigation
              activeId={this.state.activeId} 
              handleIdChange={this.handleIdChange} 
              handleMusicChange={this.handleMusicChange}
              handleInfoChange={this.handleInfoChange}
            />
          </div>
        </div>
      </div>
    );
  }

  renderInfo() {
    return (
      <div>
        <MapMusic dataAppToMap = {0}/>
        <div>
          <Cursor />
          <div>
            <About />
          </div>
        </div>
      </div>
    );
  }

  render() {
    const { width } = this.state;
    const isMobile = width <= 500;

    if (isMobile) {
      return (
        <div>Ahoj mobil</div>
      );
    } else {

      if (this.state.info) {
        console.log('info');
        return (
          <div>
              {this.renderInfo()}
            </div>
        );
      } else {

        if (this.state.music) {
          console.log('music');
          return (
            <div>
              {this.renderMusic()}
            </div>
          );
        } else {
          console.log('static');
          return (
            <div>
              {this.renderStatic()}
            </div>
          );
        }
      }
    }
  }
}

export default App;
