import React from 'react';

let objects = [ //zobrazuje sa to naopak (posledna je prva)
  null, null, null, null, null,
  null, './data/staredvere.obj', null, null, null,
  null, null, './data/pneumatiky.obj', null, null,
  null, null, './data/lavicka-istropolis.obj', './data/trznica.obj', null,
  null, './data/elektrika-skodovka1.obj', './data/elektrika-skodovka2.obj', null, null,
  null, './data/hydrant.obj', './data/kos-slobody1.obj', './data/kos-slobody2.obj', null,
  null, './data/pozor.obj', './data/dvaaviacstromov.obj', null, null,
  null, null, './data/kmenakonike.obj', './data/pristavnymost.obj', null,  //middle
  './data/strombielyplot1.obj','./data/strombielyplot2.obj', './data/strombielyplot3.obj', './data/kvetinacnabetone1.obj', './data/kvetinacnabetone2.obj',
  null, './data/kachlicky-prirodovedecka.obj', './data/lampa.obj', null, null,
  null, './data/stromsbrectanom.obj', './data/lavicka.obj', null, null,
  null, null,'./data/stromx.obj', './data/tehlovystlpik1.obj', './data/tehlovystlpik2.obj',
  null, './data/elektrika-jakubovo.obj', null, null, null,
  null, './data/pamatnik-deti.obj', './data/pamatnik-kruh.obj', './data/pamatnik-mec.obj', null,
  null, null, null, './data/promenada.obj', null,
];

var audioEl;

class Sounds extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      activeId: props.dataAppToSounds,
    }
  }

  static getDerivedStateFromProps(props, state) {
    return {activeId: props.dataAppToSounds};
  }

  componentDidMount() {
    audioEl = document.getElementsByClassName("audio-element")[0];
    audioEl.loop = true;
    audioEl.play();
  }

  componentDidUpdate() {
    audioEl.pause();
    audioEl.load();
    audioEl.play();
  }

  render() {
    return (
      <div>
        <audio className="audio-element">
          <source src={objects[this.state.activeId-1]}></source>
        </audio>
      </div>
    )
  }
}

export default Sounds;
