import React, { Component } from 'react'
// import ReactDOM from 'react-dom'
// import './app.css'
// import FragmentMatrix from './fragmentMatrix.js'
import ThreeStatic from './ThreeStatic'

let id = 0

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeId: 0
    }
    id = this.state.activeId
  }

  handleIdChange = (index) => {
    this.setState({activeId: index})
  }

  render() {
    return (
      <div>
        <ThreeStatic activeId={this.state.activeId} handleIdChange={this.handleIdChange}/>
      </div>
    )
  }
}

export default App;
