// import { Map } from 'mapbox-gl';
import React, { Component } from 'react'
import Navigation from './Navigation.js'
import ThreeStatic from './ThreeStatic.js'
import ThreeMusic from './ThreeMusic.js'
import Map from './Map.js'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeId: 38,
      music: false
    }
    //this.handleMusicChange = this.handleMusicChange.bind(this);
  }

  handleIdChange = (index) => {
    this.setState({activeId: index})
  }

  handleMusicChange = (boo) => {
    this.setState({music: boo})
  }

  sceneBuilder() {
    if (this.state.music) {
      this.renderMusic();
    } else {
      this.renderStatic();
    }
  }

  renderStatic() {
    return (
      <div>
        <Map dataAppToMap = {this.state.activeId}/>
        <div>
          <ThreeStatic dataAppToThreeStatic = {this.state.activeId}/>
          <div>
            <Navigation
              activeId={this.state.activeId} 
              handleIdChange={this.handleIdChange} 
              musicChange={this.handleMusicChange}
            />
          </div>
        </div>
      </div>

    )
  }

  renderMusic() {
    return (
      <div>
        {/* tu bude pozadie */}
        <div>
          <ThreeMusic dataAppToThreeMusic = {this.state.activeId}/>
          <div>
          <Navigation
              activeId={this.state.activeId} 
              handleIdChange={this.handleIdChange} 
              handleMusicChange={this.handleMusicChange}
            />
          </div>
        </div>
      </div>

    )
  }

  render() {
    return (
      <div>
        <Map dataAppToMap = {this.state.activeId}/>
        <div>
          <ThreeStatic dataAppToThreeStatic = {this.state.activeId}/>
          <div>
            <Navigation activeId={this.state.activeId} handleIdChange={this.handleIdChange}/>
          </div>
        </div>
      </div>

    )
  }
}

export default App;
