import React from "react";
import * as THREE from "three";
import { OBJLoader } from "three/examples/jsm/loaders/OBJLoader";
import "./three.css";

let scene; // = new THREE.Scene();
let camera = new THREE.PerspectiveCamera( 45, window.innerWidth / window.innerHeight, 1, 2000 );
let renderer; // = new THREE.WebGLRenderer( { alpha: true } );

var myObjPromise;

const circle = new THREE.TextureLoader().load( './data/circle.png' );

class ThreeStatic extends React.Component {

    constructor(props) {
        super(props); 
        this.state = {
            activeId: props.dataAppToThreeStatic,
            //scene: new THREE.Scene(),
            // camera: new THREE.PerspectiveCamera( 45, window.innerWidth / window.innerHeight, 1, 2000 ),
            // renderer: new THREE.WebGLRenderer( { alpha: true } )
        }
        // this.state.camera.position.z =250;

        // this.state.renderer.setSize( window.innerWidth, window.innerHeight );
        // this.state.renderer.setPixelRatio( window.devicePixelRatio );
        // this.state.renderer.setClearColor( 0x000000, 0);
    }

    static getDerivedStateFromProps(props, state) {
        return {activeId: props.dataAppToThreeStatic}
    }

    threeObject() {
        scene = new THREE.Scene();
        renderer = new THREE.WebGLRenderer( { alpha: true } );

        camera.position.z =250;

        renderer.setSize( window.innerWidth, window.innerHeight );
        renderer.setPixelRatio( window.devicePixelRatio );
        renderer.setClearColor( 0x000000, 0);

        this.mount.appendChild( renderer.domElement );

        var activeId = this.state.activeId;

        var mouseX = 0, mouseY = 0;
        var windowHalfX = window.innerWidth / 2;
        var windowHalfY = window.innerHeight / 2;

        //
        //
        // OBJ LOADER & MODEL SETUP

        var loadModel = function(url) {
            return new Promise(resolve => {
                new OBJLoader().load(url, resolve);
            }); 
        }

        // var myObjPromise;

        if (activeId % 7 === 0) {
            myObjPromise = loadModel('./data/strom-61669.obj');
        } else if (activeId % 7 === 1) {
            myObjPromise = loadModel('./data/lavicka-61186.obj');
        } else if (activeId % 3 === 2) {
            myObjPromise = loadModel('./data/pozor-48737.obj');
        } else if (activeId % 7 === 3) {
            myObjPromise = loadModel('./data/1.obj');
        } else if (activeId % 7 === 4) {
            myObjPromise = loadModel('./data/2.obj');
        } else if (activeId % 7 === 5) {
            myObjPromise = loadModel('./data/3.obj');
        } else {
            myObjPromise = loadModel('./data/4.obj');
        }

        myObjPromise.then(myObj => {
            var material = myObj.children[0].material;

            // const circle = new THREE.TextureLoader().load( './data/circle.png' );

            material.size = 3;
            material.sizeAttenuation = true;
            material.map = circle;
            material.alphaTest = 0.5;
            material.transparent = true;

            myObj.scale.x = 100;
            myObj.scale.y = 100;
            myObj.scale.z = 100;

            scene.add(myObj);
            material.dispose();
        });

        //
        //
        // HELPER FUNCTIONS

        var onWindowResize = function() {
            windowHalfX = window.innerWidth / 2;
            windowHalfY = window.innerHeight / 2;

            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();

            renderer.setSize( window.innerWidth, window.innerHeight );
        }

        var onDocumentMouseMove = function(event) {
            mouseX = ( - event.clientX + windowHalfX ) / 2;
            mouseY = ( - event.clientY + windowHalfY ) / 2;
        }

        document.addEventListener( 'mousemove', onDocumentMouseMove );
        window.addEventListener( 'resize', onWindowResize );

        //
        //
        // ANIMATE

        var animate = function () {
            requestAnimationFrame( animate );

            // myObjPromise.then(myObj => {
            //   scene.add( myObj );
            // });

            camera.position.x += ( mouseX - camera.position.x ) * .05;
            camera.position.y += ( - mouseY - camera.position.y ) * .05;

            camera.lookAt( scene.position );

            renderer.render( scene, camera );
        };

        animate();

        //return () => myObjPromise.remove();
    }

    dispose() {
        myObjPromise.then(myObj => {
            scene.remove(myObj);
            myObj.children[0].material.dispose();
            myObj.children[0].geometry.dispose();
        });

        //renderer.renderLists.dispose();
        renderer && renderer.renderLists.dispose();
        renderer && renderer.dispose();

        //renderer.domElement.parentElement.removeChild(renderer.domElement);		
        this.mount.removeChild( renderer.domElement );

        myObjPromise = null;
		scene = null;
        //camera = null;
        renderer = null;
    }

    componentDidMount() {
        if (myObjPromise) {
            this.dispose();
        }
        this.threeObject();
    }

    componentDidUpdate() {
        this.dispose();
        this.threeObject()
    }

    componentWillUnmount() {
        this.dispose();
    }

    render() {
        return (
            <div ref={ref => (this.mount = ref)} className = "object"/>
        )
    } 
}

export default ThreeStatic;
