import React from 'react';

let objects = [ //zobrazuje sa to naopak (posledna je prva)
    null, null, null, null, null,
    null, null, null, null, null,
    null, null, null, null, null,
    null, null, null, null, null,
    null, null, null, null, null,
    null, './data/hydrant.obj', null, null, null,
    null, './data/pozor.obj', './data/dvaaviacstromov.obj', null, null,
    null, null, './data/sounds/pneumatika.m4a', './data/sounds/horarska.m4a', null,  //middle
    './data/strombielyplot1.obj','./data/strombielyplot2.obj', './data/strombielyplot3.obj', './data/kvetinacnabetone1.obj', './data/kvetinacnabetone2.obj',
    null, null, './data/lampa.obj', null, null,
    null, './data/stromsbrectanom.obj', './data/lavicka.obj', null, null,
    null, null,'./data/stromx.obj', './data/tehlovystlpik1.obj', './data/tehlovystlpik2.obj',
    null, null, null, null, null,
    null, null, null, null, null,
    null, null, null, null, null,
];

class Sounds extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      activeId: props.dataAppToSounds,
      audioEl: document.getElementsByClassName("audio-element")[0],
    }
    this.state.audioEl.loop = true;
  }

  static getDerivedStateFromProps(props, state) {
    return {activeId: props.dataAppToSounds};
  }

  componentDidMount() {
    // const audioEl = document.getElementsByClassName("audio-element")[0];
    // audioEl.loop = true;
    this.state.audioEl.play();
  }

  componentDidUpdate() {
    this.state.audioEl.play();
  }

  render() {
    return (
      <div>
        <audio className="audio-element">
          <source src={objects[this.state.activeId-1]}></source>
        </audio>
      </div>
    )
  }
}

export default Sounds;
