import React from 'react';
import mapboxgl from 'mapbox-gl';
import './map.css';

//let discoveredIds = {};
let locationLat = [
  null, null, null, null, null,
  null, 48.151375, null, null, null,
  null, null, 48.150013, null, null,
  null, null, 48.159040, 48.157362, null,
  null, 48.141410, 48.141410, null, null,
  null, 48.144785, 48.152713, 48.152713, null,
  null, 48.144238, 48.136274, null, null,
  null, null, 48.136514, 48.133760, null,  //middle
  48.145517, 48.145517, 48.145517, 48.145403, 48.145403,
  null, 48.149632, 48.146260, null, null,
  null, 48.142108, 48.142460, null, null,
  null, null, 48.136274, 48.144397,  48.144397,
  null, 48.143821, null, null, null,
  null, 48.159203, 48.159203, 48.159203, null,
  null, null, null, 48.143599, null,
];
let locationLng = [
  null, null, null, null, null,
  null, 17.121254, null, null, null,
  null, null, 17.126111, null, null,
  null, null, 17.126109, 17.125438, null,
  null, 17.112283, 17.112283, null, null,
  null, 17.124064, 17.112654, 17.112654, null,
  null, 17.101129, 17.121612, null, null,
  null, null, 17.123316, 17.138520, null,  //middle
  17.115445, 17.115445, 17.115445, 17.115274, 17.115274,
  null, 17.069666, 17.114731, null, null,
  null, 17.102971, 17.102836, null, null,
  null, null, 17.121612, 17.118235, 17.118235,
  null, 17.121141, null, null, null,
  null, 17.117899, 17.117899, 17.117899, null,
  null, null, null, 17.076390, null,
];

let previousMusic = false;

mapboxgl.accessToken = 'pk.eyJ1Ijoic2FtLWFudCIsImEiOiJSLUx1SURJIn0.QTPlnr_Xse6dYc96m47H4A';

class Map extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      activeId: props.dataAppToMap1,
      music: props.dataAppToMap2,
      map: null,
      lng: locationLng[props.dataAppToMap1-1],
      lat: locationLat[props.dataAppToMap1-1],
      zoom: 14.9
    }

    this.mapContainer = React.createRef();
  }

  static getDerivedStateFromProps(props, state) {
    return {activeId: props.dataAppToMap1, music: props.dataAppToMap2};
  }

  componentDidMount() {
    const { lng, lat, zoom } = this.state;

    var mapstyle;

    if (this.state.music) {
      mapstyle = 'mapbox://styles/sam-ant/ckosom8xq3fsk17kc1pevhryl';
    } else {
      mapstyle = 'mapbox://styles/sam-ant/cknyvea7s1tmy17pj49f2i8gl';
    }

    const mapip = new mapboxgl.Map({
      container: this.mapContainer.current,
      style: mapstyle,
      center: [lng, lat],
      zoom: zoom 
    });

    this.setState({map:mapip});
  }

  componentDidUpdate() {
    let { lng, lat, zoom } = this.state;
    if (previousMusic != this.state.music) {
      var mapstyle;

      if (this.state.music) {
        mapstyle = 'mapbox://styles/sam-ant/ckosom8xq3fsk17kc1pevhryl';
      } else {
        mapstyle = 'mapbox://styles/sam-ant/cknyvea7s1tmy17pj49f2i8gl';
      }

      const mapip = new mapboxgl.Map({
        container: this.mapContainer.current,
        style: mapstyle,
        center: [lng, lat],
        zoom: zoom 
      });

      this.setState({map:mapip});
    }

    this.setState({lng: locationLng[this.state.activeId-1], lat: locationLat[this.state.activeId-1]})
    this.state.map.flyTo({center: [this.state.lng, this.state.lat], zoom: this.state.zoom, essential:true});
  }

  render() {
    return <div ref={this.mapContainer} className="map-container" />
  }
}

export default Map;
