// import { Map } from 'mapbox-gl';
import React, { Component } from 'react'
import Navigation from './Navigation.js'
import ThreeStatic from './ThreeStatic.js'
import ThreeMusic from './ThreeMusic.js'
import Map from './Map.js'

const Cursor = () => {
  const [position, setPosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const addEventListeners = () => {
      document.addEventListener('mousemove',mMove);
    };

    const removeEventListeners = () => {
      document.removeEventListener('mousemove', mMove);

    };

    const mMove = (el) => {
      setPosition({ x: el.clientX, y: el.clientY });
    };

    addEventListeners();
    return () => removeEventListeners();
  }, []);

  return (
    <div
      className={'cursor'}
      style={{
        left: `${position.x}px`,
        top: `${position.y}px`,
      }}
    />
  );
};

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeId: 38,
      music: false
    }
    //this.handleMusicChange = this.handleMusicChange.bind(this);
  }

  handleIdChange = (index) => {
    this.setState({activeId: index})
  }

  handleMusicChange = (boo) => {
    this.setState({music: boo})
  }

  renderStatic() {
    console.log('som v renderstatic');
    return (
      <div>
        <Map dataAppToMap = {this.state.activeId}/>
        <div>
          <ThreeStatic dataAppToThreeStatic = {this.state.activeId}/>
          <div>
            <Cursor />
            <Navigation
              activeId={this.state.activeId} 
              handleIdChange={this.handleIdChange} 
              handleMusicChange={this.handleMusicChange}
            />
          </div>
        </div>
      </div>
    );
  }

  renderMusic() {
    console.log('som v rendermusic');
    return (
      <div>
        {/* tu bude pozadie */}
        <div>
          <ThreeMusic dataAppToThreeMusic = {this.state.activeId}/>
          <div>
            <Navigation
              activeId={this.state.activeId} 
              handleIdChange={this.handleIdChange} 
              handleMusicChange={this.handleMusicChange}
            />
          </div>
        </div>
      </div>
    );
  }

  render() {
    if (this.state.music) {
      return (
        <div>
          {this.renderMusic()}
        </div>
      );
    } else {
      return (
        <div>
          {this.renderStatic()}
        </div>
      );
    }
  }
}

export default App;
